public class NewsContentSearchAction extends DispatchAction {
	/*
	 * Generated Methods
	 */
	//private VideoTypeManager videoTypeManager;
	
	private ContentManager contentManager;
	
	private NewsManager newsManager;
	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward top(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		//System.out.println(" News Content Search Action...");

		String id = request.getParameter("id");
		
		try {
			
			List list = contentManager.
				getTopContent(new Short("2"), Integer.parseInt(id), null, 0, 10);

			request.setAttribute( "NEWS_CONTENT_LIST", list);
			
			
			return mapping.findForward("top");

		}catch( Exception e ){
			e.printStackTrace();
			request.setAttribute("FAIL_MESSAGE", "系统错误，请稍后再试。");
			return mapping.findForward("fail");
		}
	}
	public ContentManager getContentManager() {
		return contentManager;
	}
	public void setContentManager(ContentManager contentManager) {
		this.contentManager = contentManager;
	}
	
	public ActionForward list(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		
		String orderby = "pubdate DESC";
		String pageNo  = request.getParameter("pno");
		String id 	   = request.getParameter("id");
		
		Map map = new TreeMap();
		map.put("id", id);
		map.put("orderby", orderby);
		request.setAttribute("PARA", map);
		
		int iPageNo = 1;
		try {
			if( pageNo != null ){
				iPageNo = Integer.parseInt( pageNo );
			}
		} catch( Exception e ){ }

		List list = null;
		Pager pager = new Pager();
		pager.setCntOfPage( 5 );
		pager.setCurPage( iPageNo );
		
		try {
			
			News news = newsManager.getNews(Integer.parseInt(id));
			request.setAttribute( "NEWS", news);
			
			list = contentManager.getContentByPage(pager, iPageNo, new Short("2"), Integer.parseInt(id), orderby);

			request.setAttribute( "NEWS_CONTENT_LIST", list);
			//System.out.println(list.size());
			request.setAttribute( "PAGER", pager );
			return mapping.findForward("list");

		}catch( Exception e ){
			e.printStackTrace();
			request.setAttribute("FAIL_MESSAGE", "系统错误，请稍后再试。");
			return mapping.findForward("fail");
		}
	}
	public NewsManager getNewsManager() {
		return newsManager;
	}
	public void setNewsManager(NewsManager newsManager) {
		this.newsManager = newsManager;
	}

	

	
	
	
	
	
}